"use client";

import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const UserHomePage = () => {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const [loadingUser, setLoadingUser] = useState(true);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const userId = localStorage.getItem("userId"); // Lấy userId từ localStorage

  useEffect(() => {
    if (!token || !userId) {
      navigate("/login");
      return;
    }

    // Lấy thông tin người dùng
    const fetchUserProfile = async () => {
      setLoadingUser(true);
      try {
        const response = await axios.get(`http://localhost:9999/users/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data); // Đảm bảo response.data có username, email, role
      } catch (error) {
        console.error("Error fetching user profile:", error);
        toast.error("Failed to load user profile. Please try again or login.");
        if (error.response?.status === 401 || error.response?.status === 404) {
          localStorage.removeItem("token");
          localStorage.removeItem("userId");
          navigate("/login");
        }
      } finally {
        setLoadingUser(false);
      }
    };

    // Lấy danh sách sách
    const fetchBooks = async () => {
      setLoadingBooks(true);
      try {
        const response = await axios.get("http://localhost:9999/books", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
        toast.error("Failed to load books. Please try again.");
      } finally {
        setLoadingBooks(false);
      }
    };

    fetchUserProfile();
    fetchBooks();
  }, [token, userId, navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    toast.success("Logged out successfully!");
    setTimeout(() => navigate("/login"), 2000);
  };

  // Hiển thị loading nếu cả hai API chưa hoàn thành
  if (loadingUser || loadingBooks) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-3">Loading data...</p>
      </div>
    );
  }

  // Hiển thị lỗi nếu user không được tải thành công
  if (!user) {
    return (
      <div className="container py-5 text-center">
        <div className="alert alert-danger" role="alert">
          Unable to load user data. Please login again.
        </div>
        <button className="btn btn-primary" onClick={() => navigate("/login")}>
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <ToastContainer />

      {/* Header Section */}
      <div className="row mb-5">
        <div className="col-12">
          <div className="bg-success text-white rounded-3 shadow p-4 text-center">
            <h1 className="display-5 fw-bold">Welcome, {user.username || "User"}!</h1>
            <p className="lead">
              Role: {user.role || "N/A"} | Email: {user.email || "N/A"}
            </p>
            <button className="btn btn-outline-light mt-2" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* User Dashboard */}
      <div className="row">
        <div className="col-12">
          <div className="card shadow border-0">
            <div className="card-header bg-light">
              <h3 className="mb-0">Your Book Collection</h3>
            </div>
            <div className="card-body">
              <p className="text-muted mb-4">
                Explore the books available in our library. Enjoy reading!
              </p>

              {/* Books Grid */}
              <div className="row row-cols-1 row-cols-md-3 g-4">
                {books.length > 0 ? (
                  books.map((book) => (
                    <div key={book._id} className="col">
                      <div className="card h-100">
                        <div
                          className="card-img-top position-relative"
                          style={{ height: "300px", overflow: "hidden" }} // Giống BookDetails
                        >
                          {book.image ? (
                            <img
                              src={book.image}
                              alt={`${book.title} cover`}
                              className="img-fluid rounded-3"
                              style={{ maxHeight: "100%", maxWidth: "100%", objectFit: "cover" }}
                              loading="lazy"
                              onError={(e) => {
                                e.target.style.display = "none";
                                e.target.nextSibling.style.display = "block";
                              }}
                            />
                          ) : null}
                          <div
                            className="text-center p-4 border rounded bg-white"
                            style={{
                              display: book.image ? "none" : "block",
                              position: "absolute",
                              top: 0,
                              left: 0,
                              right: 0,
                              bottom: 0,
                            }}
                          >
                            <i className="bi bi-book" style={{ fontSize: "5rem" }}></i>
                            <h5 className="mt-3">{book.title}</h5>
                          </div>
                        </div>
                        <div className="card-body text-center">
                          <h5 className="card-title">{book.title}</h5>
                          <p className="card-text text-muted">
                            by {book.author?.name || "Unknown Author"}
                          </p>
                          <p className="card-text">
                            <small className="text-muted">
                              {book.category?.name || "N/A"}
                            </small>
                          </p>
                          <p className="card-text">
                            Stock: {book.stock || 0} |{" "}
                            {book.available ? (
                              <span className="badge bg-success">Available</span>
                            ) : (
                              <span className="badge bg-danger">Unavailable</span>
                            )}
                          </p>
                          <a
                            href={`/book/${book._id}`}
                            className="btn btn-outline-primary mt-auto"
                          >
                            View Details
                          </a>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="col-12 text-center py-4">
                    <p className="text-muted">No books available at the moment.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHomePage;
