"use client";

import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const UserHomePage = () => {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    // Lấy thông tin người dùng
    const fetchUserProfile = async () => {
      try {
        const response = await axios.get("http://localhost:9999/users/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data);
      } catch (error) {
        console.error("Error fetching user profile:", error);
        if (error.response?.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
        }
      }
    };

    // Lấy danh sách sách
    const fetchBooks = async () => {
      try {
        const response = await axios.get("http://localhost:9999/books", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
        toast.error("Failed to load books. Please try again.");
      }
    };

    fetchUserProfile();
    fetchBooks();
  }, [token, navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    toast.success("Logged out successfully!");
    setTimeout(() => navigate("/login"), 2000);
  };

  if (!user) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-3">Loading user data...</p>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <ToastContainer />

      {/* Header Section */}
      <div className="row mb-5">
        <div className="col-12">
          <div className="bg-success text-white rounded-3 shadow p-4 text-center">
            <h1 className="display-5 fw-bold">Welcome, {user.username}!</h1>
            <p className="lead">
              Role: {user.role} | Email: {user.email}
            </p>
            <button className="btn btn-outline-light mt-2" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* User Dashboard */}
      <div className="row">
        <div className="col-12">
          <div className="card shadow border-0">
            <div className="card-header bg-light">
              <h3 className="mb-0">Your Book Collection</h3>
            </div>
            <div className="card-body">
              <p className="text-muted mb-4">
                Explore the books available in our library. Enjoy reading!
              </p>

              {/* Books Grid */}
              <div className="row row-cols-1 row-cols-md-3 g-4">
                {books.length > 0 ? (
                  books.map((book) => (
                    <div key={book._id} className="col">
                      <div className="card h-100">
                        <div className="card-body text-center">
                          <h5 className="card-title">{book.title}</h5>
                          <p className="card-text text-muted">
                            by {book.author?.name}
                          </p>
                          <p className="card-text">
                            <small className="text-muted">
                              {book.category?.name}
                            </small>
                          </p>
                          <p className="card-text">
                            Stock: {book.stock} |{" "}
                            {book.available ? (
                              <span className="badge bg-success">Available</span>
                            ) : (
                              <span className="badge bg-danger">Unavailable</span>
                            )}
                          </p>
                          <a
                            href={`/book/${book._id}`}
                            className="btn btn-outline-primary mt-auto"
                          >
                            View Details
                          </a>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="col-12 text-center py-4">
                    <p className="text-muted">No books available at the moment.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHomePage;
